package package2;

public class Assignment4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=6;
		int sum=a+b;
		int difference=a-b;
		int product=a*b;
		int quotient=a/b;
		int modular=a%b;
		System.out.println("sum of the numbers is:"+sum);
		System.out.println("difference of the numbers is:"+difference);
		System.out.println("product of the numbers is:"+product);
		System.out.println("quotient of the numbers is:"+quotient);
		System.out.println("modular output of the numbers is:"+modular);
		

	}

}
